import{_ as e,o as a,c as i,V as r}from"./chunks/framework.75ed6a74.js";const l=JSON.parse('{"title":"IOC","description":"","frontmatter":{},"headers":[{"level":2,"title":"控制反转","slug":"控制反转","link":"#控制反转","children":[{"level":3,"title":"IOC 与 DI 的关系","slug":"ioc-与-di-的关系","link":"#ioc-与-di-的关系","children":[]}]},{"level":2,"title":"依赖为什么需要注入","slug":"依赖为什么需要注入","link":"#依赖为什么需要注入","children":[]},{"level":2,"title":"IOC-vs-AOP","slug":"ioc-vs-aop","link":"#ioc-vs-aop","children":[]}],"relativePath":"design-pattern/ioc/readme.md","filePath":"design-pattern/ioc/readme.md"}'),t={name:"design-pattern/ioc/readme.md"},n=[r('<h1 id="ioc" tabindex="-1">IOC <a class="header-anchor" href="#ioc" aria-label="Permalink to &quot;IOC&quot;">​</a></h1><ul><li><a href="https://mp.weixin.qq.com/s/GXv7JwBbJ0b_AUMvZ8C1QQ" target="_blank" rel="noreferrer">要搞清楚 IoC ，那就用 TS 手写一个！</a></li><li>reflect-metadata</li><li>inversify</li></ul><h2 id="控制反转" tabindex="-1">控制反转 <a class="header-anchor" href="#控制反转" aria-label="Permalink to &quot;控制反转&quot;">​</a></h2><ul><li>IOC（控制反转）是一种编程思想，可以解耦组件，提高组件复用性。</li><li>DI（依赖注入）</li></ul><h3 id="ioc-与-di-的关系" tabindex="-1">IOC 与 DI 的关系 <a class="header-anchor" href="#ioc-与-di-的关系" aria-label="Permalink to &quot;IOC 与 DI 的关系&quot;">​</a></h3><p>DI 是实现 IOC 编程思想的一种方式。</p><p>除了 DI 外，另一种实现方式是 Dependency Lookup（依赖查找），简称 DL。</p><p>相关仓库</p><ul><li><a href="https://github.com/zhang740/power-di" target="_blank" rel="noreferrer">power-di</a></li><li><a href="https://www.npmjs.com/package/inversify" target="_blank" rel="noreferrer">InversifyJS</a></li></ul><h2 id="依赖为什么需要注入" tabindex="-1">依赖为什么需要注入 <a class="header-anchor" href="#依赖为什么需要注入" aria-label="Permalink to &quot;依赖为什么需要注入&quot;">​</a></h2><p>依赖注入（Dependency Injection，简称 DI）并不算一个复杂的概念，但想要真正理解它背后的原理却不是一件容易的事情，它的上游有更加抽象的 IOC 设计思想，下游有更加具体的 AOP 编程思想和装饰器语法，只有搞清楚整个知识脉络中各个术语之间的联系，才能够建立相对完整的认知，从而在适合的场景使用它，核心概念的关系如下图所示：</p><p><img src="/assets/DI.1b0961a2.png" alt="Dependency Injection"></p><h2 id="ioc-vs-aop" tabindex="-1">IOC-vs-AOP <a class="header-anchor" href="#ioc-vs-aop" aria-label="Permalink to &quot;IOC-vs-AOP&quot;">​</a></h2><p>参考:</p><ul><li><a href="https://zhuanlan.zhihu.com/p/141204279" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/141204279</a></li><li><a href="https://www.jianshu.com/p/3c9371e180e7" target="_blank" rel="noreferrer">使用 IOC 解耦 React 组件</a></li></ul>',15)];const o=e(t,[["render",function(e,r,l,t,o,h){return a(),i("div",null,n)}]]);export{l as __pageData,o as default};
