import{_ as l,o as i,c as t,V as r}from"./chunks/framework.75ed6a74.js";const a=JSON.parse('{"title":"软技能","description":"","frontmatter":{},"headers":[{"level":3,"title":"思维模型","slug":"思维模型","link":"#思维模型","children":[]},{"level":3,"title":"个人成长","slug":"个人成长","link":"#个人成长","children":[]},{"level":3,"title":"产品相关","slug":"产品相关","link":"#产品相关","children":[]},{"level":3,"title":"团队相关","slug":"团队相关","link":"#团队相关","children":[]}],"relativePath":"softskill/index.md","filePath":"softskill/index.md"}'),e={name:"softskill/index.md"},n=[r('<h1 id="软技能" tabindex="-1">软技能 <a class="header-anchor" href="#软技能" aria-label="Permalink to &quot;软技能&quot;">​</a></h1><ul><li>思维模型</li><li>个人成长</li><li>产品相关</li><li>团队相关</li></ul><h3 id="思维模型" tabindex="-1">思维模型 <a class="header-anchor" href="#思维模型" aria-label="Permalink to &quot;思维模型&quot;">​</a></h3><p>点状思维 ==&gt; 逻辑思维 ==&gt; 构思思维 ==&gt; 模型思维 散落大脑 桂发 无法连接 随时忘记</p><ul><li>四象限法则——时间管理</li><li>PDCA 循环——质量管理</li><li>SMART 原则——目标管理</li><li>STAR 分析法——关键事件</li><li>5W2H 分析法——思考方法 <ul><li>5W、5W1H、6W1H、6W2H 各种变体</li></ul></li><li>SWOT 分析法——企业战略制定</li><li>GROW 法</li><li>WBS 任务分解法</li><li>二八法则 ——【80 / 20】法则</li><li>复利效应</li><li>360 度评测</li><li>一页 A4 纸提案</li><li>AUR/2A3R</li><li>1C4P</li></ul><h4 id="_16-大管理模型" tabindex="-1"><a href="https://mp.weixin.qq.com/s/n1I48fPvE4-V10JzlbF5Tw" target="_blank" rel="noreferrer">16 大管理模型</a> <a class="header-anchor" href="#_16-大管理模型" aria-label="Permalink to &quot;[16 大管理模型](https://mp.weixin.qq.com/s/n1I48fPvE4-V10JzlbF5Tw)&quot;">​</a></h4><ul><li><p><strong>1. 决策树</strong>：帮助项目管理人员进行复杂决策，达成多种方案并选取最优解。</p></li><li><p><strong>2. 鱼骨图分析法</strong>：通过探究问题的根本原因，有助于项目管理人员找到解决方案。</p></li><li><p><strong>3. PDCA 循环</strong>：一个用于改进过程的逐步循环式管理法，有助于项目管理人员实现目标、监控、评估和改进。</p></li><li><p><strong>4. 麦肯锡 7S 模型</strong>：有助于项目经理和 PMO 管理组织过程，利用 7S 方针保持组织效应性和巩固组织文化。</p></li><li><p><strong>5. GREP 模型</strong>：有助于项目经理和 PMO 根据项目的需要和参与方的需求调整项目管理过程，并为项目提供以质量和风险为基础的结构支持。</p></li><li><p><strong>6. 行业生命周期理论</strong>：助力项目管理人员识别行业的商业阶段，并制定合适的项目管理计划和战略。</p></li><li><p><strong>7. SWOT 分析法</strong>：以寻找一个项目的内部和外部因素的优势、缺点、机会和威胁作为切入点，分析项目的可行性和发展方向。</p></li><li><p><strong>8. 波特价值链分析</strong>：助力项目管理从业者识别项目中的价值创造点，进而优化经营管理和运营流程。</p></li><li><p><strong>9. RACI 矩阵</strong>：用于定义在项目过程中各方的角色和职责明确，从而减少沟通障碍和项目延误。</p></li><li><p><strong>10. MECE 原则</strong>：有助于将复杂任务分解成互不重叠和独立任务，并促进对项目管理整体流程的清晰度和透明度。</p></li><li><p><strong>11. SCQA 结构</strong>：能够确保在项目团队协作和沟通方面不遗漏任何问题和问题。</p></li><li><p><strong>12. 5W2H</strong>：助力项目经理了解项目需求的完整性，并将潜在的各种情况考虑在内。</p></li><li><p><strong>13. 直方图</strong>：通过易于理解的图型展示方式分析和评估项目数据的分布。</p></li><li><p><strong>14. SMART</strong>：确保项目目标具体、可度量、可实现、关联性和时间限制。</p></li><li><p><strong>15. 战略 3C 分析</strong>：助力项目经理和 PMO 了解目标市场、客户和竞争对手的局面，以制定更适合的项目管理战略。</p></li><li><p><strong>16. PEST 分析</strong>：有助于项目经理和 PMO 了解宏观经济和政治环境的变化，以及对项目的影响，并在此基础上采取相应的措施。</p></li><li><p><a href="https://zhuanlan.zhihu.com/p/438135533" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/438135533</a></p></li></ul><h3 id="个人成长" tabindex="-1">个人成长 <a class="header-anchor" href="#个人成长" aria-label="Permalink to &quot;个人成长&quot;">​</a></h3><h3 id="产品相关" tabindex="-1">产品相关 <a class="header-anchor" href="#产品相关" aria-label="Permalink to &quot;产品相关&quot;">​</a></h3><h3 id="团队相关" tabindex="-1">团队相关 <a class="header-anchor" href="#团队相关" aria-label="Permalink to &quot;团队相关&quot;">​</a></h3>',10)];const o=l(e,[["render",function(l,r,a,e,o,s){return i(),t("div",null,n)}]]);export{a as __pageData,o as default};
